<script>
export default {
  data() {
    return {
      data: [{
          header: "A key employee leaves",
          text: "Scenario planning helps you to plan for the future."
      },
      {
          header: "A ship gets stuck",
          text: "Your supply chain is stuck? By planning for all possible scenarios, you can identify other opportunities."
      },
      {
          header: "A blackout hits the city",
          text: "No power for producing goods or keeping the shop running? By planning for all possible scenarios, you can identify other opportunities."
      }],
      timer: null,
      index: 2,
    };
  },
  computed: {
    currentData: function () {
      return this.data[Math.abs(this.index) % this.data.length];
    },
  },
  methods: {
    resolve_img_url: function (path) {
      let images = require.context("../assets/", false, /\.png$|\.jpg$/);
      return images("./" + path);
    },
    startSlide: function () {
      this.timer = setInterval(this.next, 8000);
    },

    next: function () {
      this.index += 1;
    },
    prev: function () {
      this.index -= 1;
    },
  },
  mounted() {
    this.startSlide();
  },
};
</script>